// Задание 1
const task1 = function () {

  let length = document.getElementById('length').value;
  function checkLength() {
    if (length <= 0 || length > 1000) {
      alert("Не корректный ввод параметра длины");
      document.getElementById('length').value = "";
      return false;
    };
  };

  let width = document.getElementById('width').value;
  function checkWidth() {
    if (width <= 0 || width > 1000) {
      alert("Не корректный ввод параметра ширины");
      document.getElementById('width').value = "";
      return false;
    };
  };

  let height = document.getElementById('height').value;
  function checkHeight() {
    if (height <= 0 || height > 1000) {
      alert("Не корректный ввод параметра высоты");
      document.getElementById('height').value = "";
      return false;
    };
  };

  let checkedLength = checkLength();
  let checkedWidth = checkWidth();
  let checkedHeight = checkHeight();

  if (checkedLength == undefined && checkedHeight == undefined && checkedWidth == undefined) {
    let amount = Math.ceil((width * height + length * height) * 2 / 16);
    return amount;
  }
};

document.getElementById('button-task1').onclick = () => {
  const numbers = task1();
  if (numbers != undefined) {
    document.getElementById('task1-ans').innerHTML = `Вам необхоимо такое количество банок краски - ${numbers} шт`;
    return false;
  }
  else {
    return false;
  }
};


//Задание 3
let task3 = function () {
  let ticketNumber = document.getElementById('ticket').value;
  ticketNumber = ticketNumber.split('');
  let firstPart = 0;
  let secondPart = 0;
  for (i = 0; i < 3; i++) {
    firstPart = firstPart + (+ticketNumber[i]);
    secondPart = secondPart + (+ticketNumber[i + 3]);
    console.log(firstPart);
    console.log(secondPart);
  }
  if (firstPart == secondPart) {
    return true;
  }
};

document.getElementById('form-task3').onsubmit = () => {
  const result = task3();
  if (result == true) {
    document.getElementById('task3-ans').innerHTML = `YES`;
    return false;
  }
  else {
    document.getElementById('task3-ans').innerHTML = `NO`;
    return false;
  }
};


//Задание 2
let task2Checkinput = function () {

  if (isNaN(document.getElementById('A1').value) == true || document.getElementById('A1').value == "") {
    alert("Не корректный ввод параметра A1");
    document.getElementById('A1').value = "";
    return false;
  }

  if (isNaN(document.getElementById('A2').value) == true || document.getElementById('A2').value == "") {
    alert("Не корректный ввод параметра A2");
    document.getElementById('A2').value = "";
    return false;
  }
  if (isNaN(document.getElementById('A3').value) == true || document.getElementById('A3').value == "") {
    alert("Не корректный ввод параметра A3");
    document.getElementById('A3').value = "";
    return false;
  }
  if (isNaN(document.getElementById('B1').value) == true || document.getElementById('B1').value == "" || document.getElementById('B1').value <= 0) {
    alert("Не корректный ввод параметра B1");
    document.getElementById('B1').value = "";
    return false;
  }
  if (isNaN(document.getElementById('B2').value) == true || document.getElementById('B2').value == "" || document.getElementById('B2').value <= 0) {
    alert("Не корректный ввод параметра B2");
    document.getElementById('B2').value = "";
    return false;
  }
  if (isNaN(document.getElementById('B3').value) == true || document.getElementById('B3').value == "" || document.getElementById('B3').value <= 0) {
    alert("Не корректный ввод параметра B3");
    document.getElementById('B3').value = "";
    return false;
  }
}


let task2 = function () {

  let parrameters = task2Checkinput();

  if (parrameters != false) {

    let price = document.getElementById('A1').value + " " + document.getElementById('A2').value + " " + document.getElementById('A3').value;

    let weight = document.getElementById('B1').value + " " + document.getElementById('B2').value + " " + document.getElementById('B3').value;

    let priceMass = price.split(' ');
    let weightMass = weight.split(' ');

    for (i = 0; i < 3; i++) {
      priceMass[i] = +priceMass[i];
      weightMass[i] = +weightMass[i];
    }

    priceMass.sort(function (a, b) { return a - b; });
    weightMass.sort(function (a, b) { return a - b; });

    let bestAmount = 0;
    for (i = 0; i < 3; i++) {
      bestAmount = bestAmount + priceMass[i] * weightMass[i]
      console.log(bestAmount);
    }
    return bestAmount;
  }
}




document.getElementById('form-task2').onsubmit = () => {
  let result = task2();
  if( result != undefined){
  document.getElementById('task2-ans').innerHTML = `Максимальный заработок - ${result}`;
  return false;}
}