// Task 1
function DNA(variables) {

  let arr = variables.value.split('');
  let result = [];

  arr.forEach((element, index) => {
    switch (element) {
      case "A": result[index] = "T"; break;
      case "T": result[index] = "A"; break;
      case "G": result[index] = "C"; break;
      case "C": result[index] = "G"; break;
      default: result[index] = element;
    }
  });

  return result.join("");
}

document.querySelector('#task1').onsubmit = (event) => {
  event.preventDefault();

  let variables = document.querySelector("#DNA");
  let result = DNA(variables);
  document.querySelector('.ans__task5').innerHTML = result;
}


// Task 3
let reverse = false;

function changeColorfinder(arr) {
  let ind;

  arr.forEach((element, index) => {
    if (element.matches('._active') == true) {
      element.classList.remove('_active');
      ind = index;
    }
  })
  return ind;
}

function chengeColor() {
  let svetItem = [];
  svetItem = document.querySelectorAll('.svet__item');
  let ind;
  let maxLength = svetItem.length;


  if (reverse == false) {
    ind = changeColorfinder(svetItem) + 1;
    if (ind == maxLength) {
      ind = maxLength - 2;
      reverse = true;
    }
  } else {

    ind = changeColorfinder(svetItem) - 1;
    if (ind < 0) {
      ind = 1;
      reverse = false;
    }
  }

  document.getElementsByClassName('svet__item')[ind].classList.add('_active');
}

document.querySelector(".svet__button").onclick = () => {
  chengeColor();
}


